$(document).ready(function () {
   var langCode = getCookie("lang") || 'en';

   updateSelectedLanguageDisplay(langCode);

   updatePageContent(langCode);

   $('.translate, .dropdown-content a').click(function () {
      var lang = $(this).attr('key') || $(this).attr('id');
      setCookie("lang", lang, 365); 
      updatePageContent(lang);
      updateSelectedLanguageDisplay(lang);
      hideMenu(); 
   });

   function updatePageContent(lang) {
      $('.lang').each(function (index, item) {
         var key = $(this).attr('key');
         $(this).html(arrLang[lang][key]);
      });
      $('#example-content').html(arrLang[lang]['home']);
   }

   function updateSelectedLanguageDisplay(lang) {
      var imagePath = "./" + lang + "-lang-1.png";
      $('#selectedLangIcon').attr('src', imagePath);
      $('#selectedLang').hide();
   }

   function setCookie(name, value, days) {
      var expires = "";
      if (days) {
            var date = new Date();
            date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
            expires = "; expires=" + date.toUTCString();
      }
      document.cookie = name + "=" + (value || "") + expires + "; path=/";
   }

   function getCookie(name) {
      var nameEQ = name + "=";
      var ca = document.cookie.split(';');
      for (var i = 0; i < ca.length; i++) {
            var c = ca[i];
            while (c.charAt(0) == ' ') c = c.substring(1, c.length);
            if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length, c.length);
      }
      return null;
   }

   function showMenu() {
      $('#navLinks').animate({ right: '0' }, 200);
      $('#selectedLang').show();
   }

   function hideMenu() {
      $('#navLinks').animate({ right: '-200px' }, 200);
      $('#selectedLang').hide(); 
   }
});

$('.scroll-btn').on('click', function(event) {
   event.preventDefault();

   var targetId = $(this).attr('href');
   $('html, body').animate({
      scrollTop: $(targetId).offset().top
   }, 1000); 
});


